data = data.lower().split()
keywords = ['white', 'black', 'red', 'blue', 'silver', 'yellow','penang', 'advance', 'se', 'g', '(mt)']
stats = {}

for word in set(data):
    if word in keywords:
        stats[word] = data.count(word)

total_color = stats['white'] + stats['black'] + stats['silver'] + stats['red'] + stats['blue'] + stats['yellow']
total_trans = stats['(mt)'] + (stats['g'] - stats['(mt)']) + stats['se'] + stats['advance']

print('White = ' + str(stats['white']) + '| ' + str(round(stats['white']/total_color*100, 2)) + '%')
print('Black = ' + str(stats['black']) + '| ' + str(round(stats['black']/total_color*100, 2)) + '%')
print('Silver = ' + str(stats['silver']) + '| ' + str(round(stats['silver']/total_color*100, 2)) + '%')
print('Red = ' + str(stats['red']) + '| ' + str(round(stats['red']/total_color*100, 2)) + '%')
print('Blue = ' + str(stats['blue']) + '| ' + str(round(stats['blue']/total_color*100, 2)) + '%')
print('Yellow = ' + str(stats['yellow']) + '| ' + str(round(stats['yellow']/total_color*100, 2)) + '%')
print('=======================================')
print('Penang = ' + str(stats['penang']))
print('=======================================')
print('Standard G (MT) = ' + str(stats['(mt)']) + '| ' + str(round(stats['(mt)']/total_trans*100, 2)) + '%')
print('Standard G (AT) = ' + str(stats['g'] - stats['(mt)']) + '| ' + str(round((stats['g'] - stats['(mt)'])/total_trans*100, 2)) + '%')
print('SE (AT) = ' + str(stats['se']) + '| ' + str(round(stats['se']/total_trans*100, 2)) + '%')
print('Advance (AT) = ' + str(stats['advance']) + '| ' + str(round(stats['advance']/total_trans*100, 2)) + '%')


